DROP DATABASE project;
CREATE DATABASE project;
USE project;
CREATE TABLE UVA_People( 
  computing_id VARCHAR(9) NOT NULL, 
  first_name VARCHAR(40), 
  last_name VARCHAR(40), 
  name VARCHAR(80) AS (concat_ws(' ', first_name, last_name)), 
  password VARCHAR(100), 
  PRIMARY KEY(computing_id)
);

CREATE TABLE Major(
    major_name VARCHAR(50) PRIMARY KEY,
    department VARCHAR(50) NOT NULL,
    description VARCHAR(1000) NOT NULL
);

CREATE TABLE Student(
  computing_id VARCHAR(9) NOT NULL, 
  year INT NOT NULL CHECK (year >= 0),
  FOREIGN KEY (computing_id) REFERENCES UVA_People(computing_id),
  PRIMARY KEY(computing_id)
);

CREATE TABLE Professor_email_address(
  computing_id VARCHAR(9) NOT NULL, 
  email_address VARCHAR(100) NOT NULL,
  FOREIGN KEY (computing_id) REFERENCES UVA_People(computing_id),
  PRIMARY KEY (computing_id, email_address)
);

CREATE TABLE Course (
    course_id VARCHAR(10) PRIMARY KEY,
    name VARCHAR(100) NOT NULL,
    description VARCHAR(2000) NOT NULL,
    semester VARCHAR(50) NOT NULL,
    credit INT NOT NULL
);

CREATE TABLE Grade_option (
    option_name VARCHAR(50) PRIMARY KEY,
    description VARCHAR(1000) NOT NULL
);

CREATE TABLE teach (
     computing_id VARCHAR(9) NOT NULL,
     course_id VARCHAR(10) NOT NULL,
     FOREIGN KEY (course_id) REFERENCES Course(course_id),
     FOREIGN KEY (computing_id) REFERENCES UVA_People(computing_id),
     PRIMARY KEY (computing_id, course_id)
);

CREATE TABLE takes (
    computing_id VARCHAR(9) NOT NULL,
    course_id VARCHAR(10) NOT NULL,
    option_name VARCHAR(50) NOT NULL,
    FOREIGN KEY (computing_id) REFERENCES Student(computing_id),
    FOREIGN KEY (course_id) REFERENCES Course(course_id),
    FOREIGN KEY (option_name) REFERENCES Grade_option(option_name),
    PRIMARY KEY (computing_id, course_id)
);

CREATE TABLE taken (
    computing_id VARCHAR(9) NOT NULL,
    course_id VARCHAR(10) NOT NULL,
    option_name VARCHAR(50) NOT NULL,
    FOREIGN KEY (computing_id) REFERENCES Student(computing_id),
    FOREIGN KEY (course_id) REFERENCES Course(course_id),
    FOREIGN KEY (option_name) REFERENCES Grade_option(option_name),
    PRIMARY KEY (computing_id, course_id)
);

CREATE TABLE part_of(
  major_name VARCHAR(50) NOT NULL, 
  course_id VARCHAR(10) NOT NULL,
  FOREIGN KEY (major_name) REFERENCES Major(major_name),
--   FOREIGN KEY (course_id) REFERENCES Course(course_id),
  PRIMARY KEY (major_name, course_id)
);

CREATE TABLE `declare`(
    computing_id VARCHAR(9) NOT NULL,
    major_name VARCHAR(50) NOT NULL,
    FOREIGN KEY (computing_id ) REFERENCES Student(computing_id),
    FOREIGN KEY (major_name) REFERENCES Major(major_name),
    PRIMARY KEY (computing_id , major_name)
);

CREATE TABLE is_prereq(
    course_id VARCHAR(10) NOT NULL,
    prereq_course_id VARCHAR(10) NOT NULL,
--   FOREIGN KEY (course_id) REFERENCES Course(course_id),
--   FOREIGN KEY (prereq_course_id) REFERENCES Course(course_id),
    PRIMARY KEY (course_id, prereq_course_id)
);


